mapper.update:
up
date employee
set name=#{name},role=#{role},
salary=#{salary},hiredate=#{hiredate},phone=#{phone}
where id=#{id}

mapper.select:
select sum(worktime) from rollbook
where id=#{id}
GROUP by id

mapper.update:
update rollbook 
set workTime=#{workTime} , clockout=#{clockout}
where id=#{id} and clockout is null
